import React, { useState } from 'react';
import axios from 'axios';

const QuestionForm = ({ onResponse }) => {
  const [question, setQuestion] = useState("");

  const handleAsk = async () => {
    if (!question) return;

    try {
      const res = await axios.post('http://localhost:5000/ask-question', { question });
      onResponse(res.data); // pass data to parent
    } catch (err) {
      alert("Failed to fetch answer.");
      console.error(err);
    }
  };

  return (
    <div>
      <h3>Ask a Question</h3>
      <input
        type="text"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        placeholder="e.g., What are the steps of reading a paper?"
        style={{ width: '80%' }}
      />
      <button onClick={handleAsk}>Ask</button>
    </div>
  );
};

export default QuestionForm;
