import React, { useState } from 'react';
import axios from 'axios';

const QuestionForm = ({ onResponse }) => {
  const [question, setQuestion] = useState("");

  const handleAsk = async () => {
    if (!question) return;

    try {
      const res = await axios.post('http://localhost:8000/api/ask/', { question });
      onResponse(res.data); // pass data to parent
    } catch (err) {
      const errorMessage = err.response?.data?.error || "Failed to fetch answer.";
      alert(errorMessage);
      console.error('Error details:', err.response?.data);
    }
  };

  return (
    <div>
      <h3>Ask a Question</h3>
      <input
        type="text"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        placeholder="e.g., What are the steps of reading a paper?"
        style={{ width: '80%' }}
      />
      <button onClick={handleAsk}>Ask</button>
    </div>
  );
};

export default QuestionForm;
