import React from 'react';
import { BookOpen, FileText } from 'lucide-react';

const ResponseDisplay = ({ chunks, answer }) => {
  if (!answer) return null;

  return (
    <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6 sm:p-8 w-full max-w-4xl mx-auto">
      <div className="mb-6">
        <div className="flex items-center gap-3 mb-4">
          <div className="flex items-center justify-center w-10 h-10 bg-purple-100 rounded-full">
            <BookOpen className="w-5 h-5 text-purple-600" />
          </div>
          <h3 className="text-xl font-bold text-gray-900">Answer</h3>
        </div>
        <div className="bg-gray-50 rounded-lg p-6 border-l-4 border-purple-500">
          <p className="text-gray-800 leading-relaxed whitespace-pre-wrap">{answer}</p>
        </div>
      </div>
      
      {chunks && chunks.length > 0 && (
        <div>
          <h4 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
            <FileText className="w-5 h-5 text-gray-600" />
            Relevant Passages ({chunks.length})
          </h4>
          <div className="space-y-3">
            {chunks.map((chunk, index) => (
              <div 
                key={index} 
                className="bg-blue-50 border border-blue-200 rounded-lg p-4 hover:bg-blue-100 transition-colors duration-200"
              >
                <div className="flex items-start gap-3">
                  <div className="flex-shrink-0 w-6 h-6 bg-blue-600 text-white text-xs font-bold rounded-full flex items-center justify-center mt-1">
                    {index + 1}
                  </div>
                  <p className="text-gray-700 leading-relaxed flex-1">{chunk}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ResponseDisplay;
