import React, { useState } from 'react';
import { MessageSquare, Loader2, Search } from 'lucide-react';

const QuestionForm = ({ onResponse }) => {
  const [question, setQuestion] = useState("");
  const [isAsking, setIsAsking] = useState(false);

  const handleAsk = async () => {
    if (!question.trim()) return;

    setIsAsking(true);
    try {
      const response = await fetch('http://localhost:8000/api/ask/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question })
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch answer');
      }
      
      const data = await response.json();
      onResponse(data);
      setQuestion("");
    } catch (err) {
      alert(err.message || "Failed to fetch answer.");
      console.error('Error details:', err);
    } finally {
      setIsAsking(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleAsk();
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6 sm:p-8 w-full max-w-4xl mx-auto">
      <div className="text-center mb-6">
        <div className="inline-flex items-center justify-center w-16 h-16 bg-green-100 rounded-full mb-4">
          <MessageSquare className="w-8 h-8 text-green-600" />
        </div>
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Ask Questions</h2>
        <p className="text-gray-600">Ask anything about your uploaded PDF document</p>
      </div>

      <div className="space-y-4">
        <div className="relative">
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="What would you like to know about this document? (e.g., 'What are the main findings?', 'Summarize the methodology', 'What are the key conclusions?')"
            className="w-full p-4 pr-12 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none transition-all duration-200"
            rows="4"
            disabled={isAsking}
          />
          <Search className="absolute top-4 right-4 w-5 h-5 text-gray-400" />
        </div>
        
        <button
          onClick={handleAsk}
          disabled={!question.trim() || isAsking}
          className="w-full sm:w-auto bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white font-semibold py-3 px-8 rounded-lg transition-colors duration-200 flex items-center justify-center gap-2"
        >
          {isAsking ? (
            <>
              <Loader2 className="w-5 h-5 animate-spin" />
              Analyzing...
            </>
          ) : (
            <>
              <MessageSquare className="w-5 h-5" />
              Ask Question
            </>
          )}
        </button>
      </div>
    </div>
  );
};

export default QuestionForm;
